import React, { Component } from 'react'

export default class FriendRequest extends Component {

  state = {
    followers: 0
  };

  newFollower = () => {
    let newCount = this.state.followers + 1;
     this.setState({
     followers: newCount
     });
  };

  constructor(props) {
  super(props);
  this.state = {
    show : false
  }
  this.handleshow = this.handleshow.bind(this);
  }

  handleshow() {
    // toggle your show state
    // for example if your show state is false than it will be true and if true than it will be false
    //exclamation mark(!) will work as not operator
    this.setState({
      show: !this.state.show
    });
  };

  render() {
    const followers = this.state.followers;

    return (

      <div className="w3-card w3-round w3-white w3-center">
        <div className="w3-container">
          <p>Follow Jane Back?</p>
          <img src="images/janedoe.jpg" alt="Avatar" style={{width: '25%'}} /><br />
          <span>Jane Doe</span>
          <div className="w3-row w3-opacity">
            <div>
              <button onClick={this.handleshow} className="w3-button w3-block w3-green w3-section" title="Follow"><i className="fa fa-check" style={{ color: "red" }}/></button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
