import React, { Component } from 'react'

export default class NewPost extends Component {

  state = {
    count: 0
  }

  incrementMe = () => {
    let newCount = this.state.count + 1
    this.setState({
      count: newCount
    })
  }

  render() {
    return (

      <div className="w3-row-padding">
        <div className="w3-col m12">
          <div className="w3-card w3-round w3-white">
            <div className="w3-container w3-padding">
              <h6 className="w3-opacity">What's on your mind, Boomer?</h6>
              <p contentEditable="true" className="w3-border w3-padding">Status: Feeling Blue</p>
              <button onClick={this.incrementMe} type="button" className="w3-button w3-theme"><i className="fa fa-pencil" /> &nbsp;Post {this.state.count}</button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
