import React, { Component } from 'react'

class LikeButton extends React.Component {
  state = {
    likes: 0
  };

  addLike = () => {
    let newCount = this.state.likes + 1;
     this.setState({
     likes: newCount
     });
  };

  render() {
    const likes = this.state.likes;
    if (likes === 0) {
      return (
        <div>
          <button
            className="button"
            onClick={this.addLike}
          >
            <i className="fa fa-thumbs-up"  style={{ color: "#33c3f0" }}></i>
          </button>
        </div>
      );
    }
    if (likes === 1) {
      return (
        <div>
          <button className="button" onClick={this.addLike}>
            <i className="fa fa-thumbs-up" style={{ color: "red" }}></i>
          </button>
        </div>
      );
    }
    if (likes > 1) {
      return (
        <div>
          <button className="button" onClick={this.addLike}>
            <i className="fa fa-thumbs-up" style={{ color: "red" }}></i>{" "}
            {likes}
          </button>
        </div>
      );
    }
  }
}
