import React, { Component } from 'react'

export default class FollowerCount extends React.Component {
  state = {
    followers: 0
  };

  followers = () => {
    let newCount = this.state.followers + 1;
     this.setState({
     followers: newCount
     });
  };

  render() {
    const followers = this.state.followers;
      return (
        <div>
          <button
            className="button"
            onClick={this.followers}
          className="w3-button w3-theme-d1 w3-margin-bottom"><i className="fa fa-thumbs-up" /> &nbsp;Follow {this.state.followers}
          </button>
        </div>
      )

  }
}
